import React, { useState } from "react";
import axios from "axios";

function App() {
  const [role, setRole] = useState("Frontend Engineer");
  const [resume, setResume] = useState(""); // Optional preview
  const [file, setFile] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [reply, setReply] = useState("");
  const [started, setStarted] = useState(false);

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (!uploaded) return;
    setFile(uploaded);

    // Optional preview for text files only
    if (uploaded.name.endsWith(".txt")) {
      const reader = new FileReader();
      reader.onload = function (event) {
        setResume(event.target.result);
      };
      reader.readAsText(uploaded);
    } else {
      setResume("📎 PDF file selected.");
    }
  };

  const handleStart = async () => {
    if (!file) {
      alert("Please upload a resume file before starting the interview.");
      return;
    }

    const formData = new FormData();
    formData.append("role", role);
    formData.append("resumeFile", file);

    try {
      const res = await axios.post("http://localhost:8000/start-interview/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setChatHistory(res.data.history);
      setReply(res.data.question);
      setStarted(true);
    } catch (err) {
      console.error("Start interview failed:", err.response?.data || err.message);
    }
  };

  const handleSend = async () => {
    const payload = {
      history: chatHistory,
      user_answer: userAnswer,
      role,
      resume,
    };

    try {
      const res = await axios.post("http://127.0.0.1:8000/interview/", payload);
      setChatHistory(res.data.history);
      setReply(res.data.reply);
      setUserAnswer("");
    } catch (err) {
      console.error("Error sending answer:", err);
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "700px", margin: "auto" }}>
      <h2>🧠 AI Interview Simulator</h2>

      <label>
        Select Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option>Frontend Engineer</option>
          <option>Backend Developer</option>
          <option>Cloud Architect</option>
          <option>Data Analyst</option>
        </select>
      </label>

      <br /><br />
      <label>
        Upload Resume (PDF or TXT):
        <input type="file" accept=".txt,.pdf" onChange={handleFileUpload} />
      </label>

      {resume && (
        <>
          <h4>📝 Resume Preview:</h4>
          <textarea value={resume} readOnly rows={6} style={{ width: "100%" }} />
        </>
      )}

      <br />
      <button onClick={handleStart} style={{ marginTop: "1rem" }}>
        Start Interview
      </button>

      <br /><br />
      <label>
        Your Answer:
        <input
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          style={{ width: "100%" }}
          disabled={!started}
        />
      </label>

      <button onClick={handleSend} disabled={!started} style={{ marginTop: "1rem" }}>
        Send Answer
      </button>

      <br /><br />
      <div>
        <h4>💬 Interview Feedback:</h4>
        <pre style={{ background: "#f4f4f4", padding: "1rem" }}>{reply}</pre>
      </div>
    </div>
  );
}

export default App;
