import React, { useState } from "react";
import axios from "axios";
import * as pdfjsLib from "pdfjs-dist/webpack";

function App() {
  const [role, setRole] = useState("Frontend Engineer");
  const [resume, setResume] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [reply, setReply] = useState("");
  const [started, setStarted] = useState(false);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const fileType = file.name.split(".").pop();

    if (fileType === "txt") {
      const reader = new FileReader();
      reader.onload = function (event) {
        setResume(event.target.result);
      };
      reader.readAsText(file);
    } else if (fileType === "pdf") {
      const reader = new FileReader();
      reader.onload = async function () {
        const typedArray = new Uint8Array(reader.result);
        const pdf = await pdfjsLib.getDocument({ data: typedArray }).promise;

        let text = "";
        for (let i = 1; i <= pdf.numPages; i++) {
          const page = await pdf.getPage(i);
          const content = await page.getTextContent();
          const pageText = content.items.map((item) => item.str).join(" ");
          text += pageText + "\n";
        }

        setResume(text);
      };
      reader.readAsArrayBuffer(file);
    } else {
      alert("Please upload a .txt or .pdf file only.");
    }
  };

  const handleSend = async () => {
    const payload = {
      history: chatHistory,
      user_answer: userAnswer,
      resume,
      role,
    };

    try {
      const res = await axios.post("http://127.0.0.1:8000/interview/", payload);
      setChatHistory(res.data.history);
      setReply(res.data.reply);
      setUserAnswer("");
    } catch (err) {
      console.error("Error sending answer:", err);
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "700px", margin: "auto" }}>
      <h2>üß† AI Interview Simulator</h2>

      <label>
        Select Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option>Frontend Engineer</option>
          <option>Backend Developer</option>
          <option>Cloud Architect</option>
          <option>Data Analyst</option>
        </select>
      </label>

      <br /><br />
      <label>
        Upload Resume (PDF or TXT):
        <input type="file" accept=".txt,.pdf" onChange={handleFileUpload} />
      </label>

      {resume && (
        <>
          <h4>üìù Extracted Resume Text:</h4>
          <textarea value={resume} readOnly rows={6} style={{ width: "100%" }} />
        </>
      )}

      <br />
      <button onClick={handleStart} style={{ marginTop: "1rem" }}>
        Start Interview
      </button>

      <br /><br />
      <label>
        Your Answer:
        <input
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          style={{ width: "100%" }}
          disabled={!started}
        />
      </label>

      <button onClick={handleSend} disabled={!started} style={{ marginTop: "1rem" }}>
        Send Answer
      </button>

      <br /><br />
      <div>
        <h4>üí¨ Interview Feedback:</h4>
        <pre style={{ background: "#f4f4f4", padding: "1rem" }}>{reply}</pre>
      </div>
    </div>
  );
}

export default App;
