import React, { useState } from "react";
import axios from "axios";
import MicRecorder from "mic-recorder-to-mp3";

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [role, setRole] = useState("Frontend Engineer");
  const [file, setFile] = useState(null);
  const [resumeText, setResumeText] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [question, setQuestion] = useState("");
  const [feedback, setFeedback] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [started, setStarted] = useState(false);
  const [recording, setRecording] = useState(false);

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (!uploaded) return;
    setFile(uploaded);

    if (uploaded.name.endsWith(".txt")) {
      const reader = new FileReader();
      reader.onload = (event) => setResumeText(event.target.result);
      reader.readAsText(uploaded);
    } else {
      setResumeText("üìé PDF uploaded");
    }
  };

  const handleStart = async () => {
    if (!file) return alert("Please upload your resume first!");

    const formData = new FormData();
    formData.append("role", role);
    formData.append("resumeFile", file);

    try {
      const res = await axios.post("http://127.0.0.1:8000/start-interview/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setChatHistory(res.data.history);
      setQuestion(res.data.question);
      setFeedback("");
      setStarted(true);
    } catch (err) {
      console.error("Start interview failed:", err.response?.data || err.message);
    }
  };

  const handleSend = async () => {
    const payload = {
      history: chatHistory,
      user_answer: userAnswer,
      resume: resumeText,
      role,
    };

    try {
      const res = await axios.post("http://127.0.0.1:8000/interview/", payload);
      const messages = res.data.history;
      const lastReply = res.data.reply;

      const [feedbackPart, ...rest] = lastReply.split(/(?=Question|Q\d|Next question|Follow-up)/i);
      const nextQuestion = rest.join(" ").trim();

      setFeedback(feedbackPart.trim());
      setQuestion(nextQuestion || "‚úÖ Interview complete.");
      setChatHistory(messages);
      setUserAnswer("");
    } catch (err) {
      console.error("Error sending answer:", err);
    }
  };

  const startRecording = async () => {
    try {
      await recorder.start();
      setRecording(true);
    } catch (error) {
      console.error("Recording start failed:", error);
    }
  };

  const stopRecording = async () => {
    try {
      const [buffer, blob] = await recorder.stop().getMp3();
      setRecording(false);

      const formData = new FormData();
      formData.append("file", new File(buffer, "recording.mp3", { type: "audio/mpeg" }));

      const res = await axios.post("http://127.0.0.1:8000/transcribe/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setUserAnswer(res.data.transcription || "");
    } catch (err) {
      console.error("Transcription failed:", err);
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "700px", margin: "auto" }}>
      <h2>üß† AI Interview Simulator</h2>

      <label>
        Select Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option>Frontend Engineer</option>
          <option>Backend Developer</option>
          <option>Cloud Architect</option>
          <option>Data Analyst</option>
        </select>
      </label>

      <br /><br />
      <label>
        Upload Resume (PDF or TXT):
        <input type="file" accept=".txt,.pdf" onChange={handleFileUpload} />
      </label>

      <br /><br />
      <button onClick={handleStart} disabled={!file}>
        Start Interview
      </button>

      <br /><br />
      {started && (
        <>
          <h4>üßë‚Äçüíº Interviewer:</h4>
          <p>{question}</p>

          <label>
            Your Answer:
            <input
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              style={{ width: "100%" }}
              placeholder="Type or speak your answer..."
            />
          </label>

          <br />
          <button onClick={handleSend} style={{ marginTop: "1rem" }}>
            Send Answer
          </button>

          <button
            onClick={recording ? stopRecording : startRecording}
            style={{ marginTop: "1rem", marginLeft: "1rem" }}
          >
            {recording ? "üõë Stop Recording" : "üé§ Start Recording"}
          </button>

          {feedback && (
            <>
              <br />
              <h4>üìù Interviewer Feedback:</h4>
              <pre style={{ background: "#f4f4f4", padding: "1rem" }}>{feedback}</pre>
            </>
          )}
        </>
      )}
    </div>
  );
}

export default App;
