import React, { useState } from "react";
import axios from "axios";

function App() {
  const [role, setRole] = useState("Frontend Engineer");
  const [file, setFile] = useState(null);
  const [resumeText, setResumeText] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [question, setQuestion] = useState("");
  const [feedback, setFeedback] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [started, setStarted] = useState(false);
  const [recording, setRecording] = useState(false);
  const [recognitionRef, setRecognitionRef] = useState(null);

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (!uploaded) return;
    setFile(uploaded);

    if (uploaded.name.endsWith(".txt")) {
      const reader = new FileReader();
      reader.onload = (event) => setResumeText(event.target.result);
      reader.readAsText(uploaded);
    } else {
      setResumeText("📎 PDF uploaded");
    }
  };

  const handleStart = async () => {
    if (!file) return alert("Please upload your resume first!");

    const formData = new FormData();
    formData.append("role", role);
    formData.append("resumeFile", file);

    try {
      const res = await axios.post("http://127.0.0.1:8000/start-interview/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setChatHistory(res.data.history);
      setQuestion(res.data.question);
      setFeedback("");
      setStarted(true);
    } catch (err) {
      console.error("Start interview failed:", err.response?.data || err.message);
    }
  };

  const handleSend = async () => {
    const payload = {
      history: chatHistory,
      user_answer: userAnswer,
      resume: resumeText,
      role,
    };

    try {
      const res = await axios.post("http://127.0.0.1:8000/interview/", payload);
      const messages = res.data.history;
      const lastReply = res.data.reply;

      const [feedbackPart, ...rest] = lastReply.split(/(?=Question|Q\d|Next question|Follow-up)/i);
      const nextQuestion = rest.join(" ").trim();

      setFeedback(feedbackPart.trim());
      setQuestion(nextQuestion || "✅ Interview complete.");
      setChatHistory(messages);
      setUserAnswer("");
    } catch (err) {
      console.error("Error sending answer:", err);
    }
  };

  const handleVoiceInput = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support speech recognition.");
      return;
    }

    if (recognitionRef) {
      recognitionRef.stop(); // Stop previous if running
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = true;
    recognition.continuous = false;
    recognition.maxAlternatives = 1;

    setRecording(true);
    setRecognitionRef(recognition);

    recognition.start();

    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = event.resultIndex; i < event.results.length; ++i) {
        transcript += event.results[i][0].transcript;
      }
      setUserAnswer(transcript);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
      alert("Voice recognition failed: " + event.error);
      setRecording(false);
    };

    recognition.onend = () => {
      setRecording(false);
      setRecognitionRef(null);
    };
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "700px", margin: "auto" }}>
      <h2>🧠 AI Interview Simulator</h2>

      <label>
        Select Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option>Frontend Engineer</option>
          <option>Backend Developer</option>
          <option>Cloud Architect</option>
          <option>Data Analyst</option>
        </select>
      </label>

      <br /><br />
      <label>
        Upload Resume (PDF or TXT):
        <input type="file" accept=".txt,.pdf" onChange={handleFileUpload} />
      </label>

      <br /><br />
      <button onClick={handleStart} disabled={!file}>
        Start Interview
      </button>

      <br /><br />
      {started && (
        <>
          <h4>🧑‍💼 Interviewer:</h4>
          <p>{question}</p>

          <label>
            Your Answer:
            <input
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              style={{ width: "100%" }}
              placeholder="Type or speak your answer..."
            />
          </label>

          <br />
          <button onClick={handleSend} style={{ marginTop: "1rem" }}>
            Send Answer
          </button>

          <button
            onClick={handleVoiceInput}
            style={{ marginTop: "1rem", marginLeft: "1rem" }}
          >
            🎤 {recording ? "Listening..." : "Speak Answer"}
          </button>

          {feedback && (
            <>
              <br />
              <h4>📝 Interviewer Feedback:</h4>
              <pre style={{ background: "#f4f4f4", padding: "1rem" }}>{feedback}</pre>
            </>
          )}
        </>
      )}
    </div>
  );
}

export default App;
